// Prepare text for embedding
const article = $input.item.json;

// Combine title, category, tags, and content for rich embedding
const textForEmbedding = [
  `Title: ${article.title}`,
  `Category: ${article.category}/${article.subcategory}`,
  `Tags: ${(article.tags || []).join(', ')}`,
  `Content: ${article.content}`
].join('\n\n');

return {
  json: {
    article_id: article.article_id,
    title: article.title,
    text_for_embedding: textForEmbedding
  }
};

// Extract embedding from OpenAI response
const article_id = $('Prepare Text for Embedding').item.json.article_id;
const title = $('Prepare Text for Embedding').item.json.title;
const response = $input.item.json;

const embedding = response.data?.[0]?.embedding;

if (!embedding) {
  throw new Error(`Failed to get embedding for article: ${title}`);
}

// Format embedding as PostgreSQL array
const embeddingArray = '[' + embedding.join(',') + ']';

return {
  json: {
    article_id: article_id,
    title: title,
    embedding: embeddingArray,
    embedding_length: embedding.length
  }
};

// Count processed articles
const items = $input.all();

return {
  json: {
    success: true,
    articles_processed: items.length,
    message: `Successfully generated embeddings for ${items.length} articles`
  }
};

// No articles to process
return {
  json: {
    success: true,
    articles_processed: 0,
    message: 'No articles found without embeddings'
  }
};
